pub fn storage_read<T, N>(
    storage_slot: Field,
    deserialize: fn ([Field; N]) -> T,
) -> T {
    let fields = storage_read_oracle_wrapper(storage_slot);
    deserialize(fields)
}

#[oracle(storageWrite)]
fn storage_write_oracle<N>(
    _storage_slot: Field,
    _values: [Field; N],
) -> [Field; N] {}

// TODO: Remove return value.
unconstrained pub fn storage_write<N>(
    storage_slot: Field,
    fields: [Field; N]
) {
    let _hash = storage_write_oracle(storage_slot, fields);
}

